INSERT INTO `wp_4_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2022-10-14 09:14:25', '2022-10-14 09:14:25', 'Chào mừng bạn đến với <a href=\"http://localhost/ThucHanh_141022/\">Multisite Trang</a>. Đây là bài viết đầu tiên của bạn. Bạn có thể sửa hoặc xoá nó, sau đó bắt đầu viết bài nào!', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2022-10-14 09:14:25', '2022-10-14 09:14:25', '', 0, 'http://localhost/ThucHanh_141022/hihihehehaha/?p=1', 0, 'post', '', 1),
(2, 1, '2022-10-14 09:14:25', '2022-10-14 09:14:25', '<!-- wp:paragraph -->\n<p>This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p>...or something like this:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p>As a new WordPress user, you should go to <a href=\"http://localhost/ThucHanh_141022/hihihehehaha/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!</p>\n<!-- /wp:paragraph -->', 'Sample Page', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2022-10-14 09:14:25', '2022-10-14 09:14:25', '', 0, 'http://localhost/ThucHanh_141022/hihihehehaha/?page_id=2', 0, 'page', '', 0);

